import React, { Component } from "react";
import PropTypes from "prop-types";
import ShelfChanger from "./ShelfChanger";

class SearchResult extends Component {
  selectOption = (title, currRead, read, wantToRead) => {
    if (currRead && currRead.filter(bookTitle => bookTitle.title === title)) {
      return process.env.REACT_APP_CURR_READ;
    }

    if (read && read.filter(bookTitle => bookTitle.title === title)) {
      return process.env.REACT_APP_READ;
    }

    if (
      wantToRead &&
      wantToRead.filter(bookTitle => bookTitle.title === title)
    ) {
      return process.env.REACT_APP_WANT_READ;
    }
    return process.env.REACT_APP_NONE;
  };

  render() {
    const {
      searchItem,
      bookList,
      chooseOption,
      loading,
      shelf,
      optiontype
    } = this.props;

    return (
      <div className="bookshelf-books">
        <h3>{loading}</h3>
        {searchItem.length === 0 ? (
          ""
        ) : (
          <ol className="books-grid">
            {bookList && bookList.length
              ? bookList.map((bookItem, i) => (
                  <li key={i}>
                    <div className="book">
                      <div className="book-top">
                        <div
                          className="book-cover"
                          style={{
                            width: 128,
                            height: 193,
                            backgroundImage: `url(${
                              !bookItem.imageLinks
                                ? "No Image"
                                : bookItem.imageLinks.thumbnail
                            })`
                          }}
                        ></div>

                        <ShelfChanger
                          bookItem={bookItem}
                          chooseOption={chooseOption}
                          optionval={optiontype}
                          shelf={shelf(bookItem.id)}
                        />
                      </div>
                      <div className="book-title">{bookItem.title}</div>
                      <div className="book-authors">
                        {!bookItem.authors
                          ? ""
                          : bookItem.authors.map(author => author)}
                      </div>
                    </div>
                  </li>
                ))
              : ""}
          </ol>
        )}
      </div>
    );
  }
}

SearchResult.propTypes = {
  bookList: PropTypes.array.isRequired,
  searchItem: PropTypes.string.isRequired,
  chooseOption: PropTypes.func.isRequired,
  loading: PropTypes.string
};

export default SearchResult;
