import React from "react";
import * as BooksAPI from "./BooksAPI";
import "./App.css";
import Search from "./Search";
import CurrentlyReading from "./CurrentlyReading";
import WanttoRead from "./WanttoRead";
import Read from "./Read";
import AddBook from "./AddBook";
import SearchResult from "./SearchResult";
import { Route } from "react-router-dom";
import { debounce } from "debounce";
import Notification from "./Notification";
//import ApiCall from "../src/utils/ApiCall";

class BooksApp extends React.Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    books: [],
    searchItem: "",
    currentlyReading: [],
    wantToRead: [],
    read: [],
    myread: [],
    message: "",
    active: false,
    status: true,
    loading: ""
  };

  searchForBooks = event => {
    debounce(this.setState({ searchItem: event.target.value }), 500);
    this.APIDataCall(this.state.searchItem);
  };

  APIDataCall = search => {
    this.setState({
      books: []
    });

    this.setState({
      loading: "loading..."
    });
    if (search.trim() && search.trim().length > 0) {
      BooksAPI.search(search.trim())
        .then(data => {
          data.map(value =>
            this.setState({
              books: [...this.state.books, value],
              loading: ""
            })
          );
        })
        .catch(err => console.log("Book not found"));
    }
  };

  addBookCheck = booksAdd => {
    if (this.state.currentlyReading.some(el => el.id === booksAdd.id)) {
      this.setState(prevState => ({
        currentlyReading: prevState.currentlyReading.filter(
          el => el.id !== booksAdd.id
        )
      }));
      return {
        result: true,
        message: "Book successfully moved"
      };
    }
    if (this.state.wantToRead.some(el => el.id === booksAdd.id)) {
      this.setState(prevState => ({
        wantToRead: prevState.wantToRead.filter(el => el.id !== booksAdd.id)
      }));
      return {
        result: true,
        message: "Book successfully moved"
      };
    }

    if (this.state.read.some(el => el.id === booksAdd.id)) {
      this.setState(prevState => ({
        read: prevState.read.filter(el => el.id !== booksAdd.id)
      }));
      return {
        result: true,
        message: "Book successfully moved"
      };
    }
    return {
      result: true,
      message: "Book successly added"
    };
  };

  showDisplay = (message, status) => {
    this.setState({
      message,
      active: true,
      status
    });
    let seconds = 0;
    const endTimer = setInterval(() => {
      seconds += 10;
      if (seconds > 2000) {
        this.setState({
          active: false
        });
        clearInterval(endTimer);
      }
    }, 10);
  };

  chooseOption = event => {
    const { selectedIndex, options } = event.target;
    const boodID = options[selectedIndex].dataset.bookid;
    const booksAdd = this.state.books.find(book => book.id === boodID);
    const value = options[selectedIndex].text;

    const { result, message } = this.addBookCheck(booksAdd);

    if (result) {
      switch (value) {
        case process.env.REACT_APP_CURR_READ:
          this.setState({
            currentlyReading: [...this.state.currentlyReading, booksAdd]
          });

          BooksAPI.update(booksAdd, "currentlyReading");

          break;
        case process.env.REACT_APP_WANT_READ:
          this.setState({
            wantToRead: [...this.state.wantToRead, booksAdd]
          });
          BooksAPI.update(booksAdd, "wantToRead");
          break;
        case process.env.REACT_APP_READ:
          this.setState({
            read: [...this.state.read, booksAdd]
          });
          BooksAPI.update(booksAdd, "read");
          break;
        default:
          console.log("Nothing selected");
      }
      this.showDisplay(message, true);
    }

    const addItem = {
      booksAdd,
      status: value
    };

    this.setState({
      myread: [...this.state.myread, addItem]
    });
    console.log(value);
    console.log(this.state.currentlyReading);
  };

  componentDidMount() {
    //BooksAPI.search(this.state.booksSearch).then(books => console.log(books));
    //this.getBookSearchResult()
    BooksAPI.getAll().then(res => console.log(res));
    BooksAPI.getAll().then(res => {
      res.map(data =>
        this.setState({
          books: [...this.state.books, data]
        })
      );
    });
  }

  render() {
    return (
      <div className="app">
        {this.state.message.length > 0 && (
          <Notification
            message={this.state.message}
            showItem={this.state.active ? "fadeIn" : "fadeOut"}
            status={this.state.status ? "success" : "error"}
          />
        )}
        <Route
          path="/search"
          render={() => (
            <div>
              <Search
                searchItem={this.state.searchItem}
                searchForBooks={this.searchForBooks}
              />
              <SearchResult
                bookList={this.state.books}
                searchItem={this.state.searchItem}
                chooseOption={this.chooseOption}
                loading={this.state.loading}
              />
            </div>
          )}
        />

        <Route
          exact
          path="/"
          render={() => (
            <div>
              <div className="list-books">
                <div className="list-books-title">
                  <h1>{process.env.REACT_APP_MY_READ}</h1>
                </div>
                <div className="list-books-content">
                  <div>
                    <CurrentlyReading
                      books={this.state.books}
                      chooseOption={this.chooseOption}
                    />
                    <WanttoRead
                      books={this.state.books}
                      chooseOption={this.chooseOption}
                    />
                    <Read
                      books={this.state.read}
                      chooseOption={this.chooseOption}
                    />
                  </div>
                </div>
              </div>
              <AddBook />
            </div>
          )}
        />
      </div>
    );
  }
}

export default BooksApp;
