import React from "react";
import PropTypes from "prop-types";
import ShelfChanger from "./ShelfChanger";

const WanttoRead = props => {
  return (
    <div className="bookshelf">
      <h2 className="bookshelf-title">Want to Read</h2>
      <div className="bookshelf-books">
        <div>
          {props.books.map(book => (
            <p>{book.title}</p>
          ))}
        </div>
        <ol className="books-grid">
          {props.books &&
            props.books
              .filter(
                book => book.shelf === process.env.REACT_APP_WANT_READ_API_SHELF
              )
              .map((bookinner, i) => (
                <li key={i}>
                  <div className="book">
                    <div className="book-top">
                      <div
                        className="book-cover"
                        style={{
                          width: 128,
                          height: 193,
                          backgroundImage: `url(${
                            !bookinner.imageLinks
                              ? "No Image Provided"
                              : bookinner.imageLinks.thumbnail
                          })`
                        }}
                      ></div>
                      <ShelfChanger
                        bookItem={bookinner}
                        chooseOption={props.chooseOption}
                        value={process.env.REACT_APP_WANT_READ}
                      />
                    </div>
                    <div className="book-title">{bookinner.title}</div>
                    <div className="book-authors">
                      {!bookinner.authors
                        ? "Oopss! No Author"
                        : bookinner.authors.map(author => author)}
                    </div>
                  </div>
                </li>
              ))}
          <li>
            <div className="book">
              <div className="book-top">
                <div
                  className="book-cover"
                  style={{
                    width: 128,
                    height: 193,
                    backgroundImage:
                      'url("http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api")'
                  }}
                ></div>
                <div className="book-shelf-changer">
                  <select>
                    <option value="move" disabled>
                      Move to...
                    </option>
                    <option value="currentlyReading">Currently Reading</option>
                    <option value="wantToRead">Want to Read</option>
                    <option value="read">Read</option>
                    <option value="none">None</option>
                  </select>
                </div>
              </div>
              <div className="book-title">To Kill a Mockingbird</div>
              <div className="book-authors">Harper Lee</div>
            </div>
          </li>
          <li>
            <div className="book">
              <div className="book-top">
                <div
                  className="book-cover"
                  style={{
                    width: 128,
                    height: 188,
                    backgroundImage:
                      'url("http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api")'
                  }}
                ></div>
                <div className="book-shelf-changer">
                  <select>
                    <option value="move" disabled>
                      {process.env.REACT_APP_MOVE_TO}
                    </option>
                    <option name="currentlyReading" value="currentlyReading">
                      {process.env.REACT_APP_CURR_READ}
                    </option>
                    <option name="wantToRead" value="wantToRead">
                      {process.env.REACT_APP_WANT_READ}
                    </option>
                    <option name="read" value="read">
                      {process.env.REACT_APP_READ}
                    </option>
                    <option name="none" value="none">
                      {process.env.REACT_APP_NONE}
                    </option>
                  </select>
                </div>
              </div>
              <div className="book-title">Ender's Game</div>
              <div className="book-authors">Orson Scott Card</div>
            </div>
          </li>
        </ol>
      </div>
    </div>
  );
};

WanttoRead.propTypes = {
  books: PropTypes.array.isRequired
};

export default WanttoRead;
