import React, { Component } from "react";
import PropTypes from "prop-types";

class ShelfChanger extends Component {
  selectShelf = (bookId, shelf) => {
    const shelfData = shelf.filter(book => book.id === bookId);
    if (shelfData.length > 0) {
      if (shelfData[0].shelf === "currentlyReading") {
        return process.env.REACT_APP_CURR_READ;
      }

      if (shelfData[0].shelf === "read") {
        return process.env.REACT_APP_READ;
      }

      if (shelfData[0].shelf === "wantToRead") {
        return process.env.REACT_APP_WANT_READ;
      }
    } else {
      return process.env.REACT_APP_NONE;
    }
  };

  render() {
    const { chooseOption, bookItem, shelf, shelfctr } = this.props;
    return (
      <div className="book-shelf-changer">
        <select
          onChange={chooseOption}
          value={shelfctr && this.selectShelf(bookItem.id, shelf)}
        >
          <option disabled>{process.env.REACT_APP_MOVE_TO}</option>
          <option data-bookid={bookItem.id}>
            {process.env.REACT_APP_CURR_READ}
          </option>
          <option data-bookid={bookItem.id}>
            {process.env.REACT_APP_WANT_READ}
          </option>
          <option data-bookid={bookItem.id} value={process.env.REACT_APP_READ}>
            {process.env.REACT_APP_READ}
          </option>
          <option data-name={bookItem.title}>
            {process.env.REACT_APP_NONE}
          </option>
        </select>
      </div>
    );
  }
}

ShelfChanger.propTypes = {
  chooseOption: PropTypes.func.isRequired,
  bookItem: PropTypes.object.isRequired
};

export default ShelfChanger;
